package listaPara;

public class ListaPara_exercicio04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int soma = 0;
		
		for (int i = 2; i < 502; i += 2) {
			soma += i;
			System.out.println(soma);
		}
	}

}
